import React, { Suspense, useEffect, useState } from "react";
import { Container, Grid, Responsive, Button } from "semantic-ui-react";
import Navbar from "../Navbar/navbar";
import SearchBar from "../SearchBar/SearchBar";
import DashMidGrids from "../DashMidGrids/dashmidgrids";
import Profile from "../Profile/Profile";
import MainButton from "../MainButton/MainButton";
import DashSideSection from "../../sections/DashSideSection/dashsidesection";
import TabletView from "../tablet-view/tablet-view"
import { useTranslation } from "react-i18next";

const Dashboard = (props) => {
  const [flag, setFlag] = useState(true);
  const { t } = useTranslation("common");

  const changeFlag = () => {
    setFlag(false);
    setTimeout(() => {
      console.log(flag);
    }, 1000);
  };

  useEffect(() => {
    document.title = t(props.title);
    if (props.userDevices && props.userDevices.length) {
      props.getSensorsData(props.userDevices.map((d) => d.deviceId).join());
    }
  }, [props.title]);
  return (
    <div>
      <Responsive minWidth={1366}>
        <Grid columns={3}>
          <Grid.Row>
            <Grid.Column width={1}>
              {/* <Container className="leftcontainer"> */}
              <Navbar
                active="dashboard"
                // defaultsensor={
                //   props.sensors && props.sensors[0]
                //     ? props.sensors[0].deviceId
                //     : undefined
                // }
              />
              {/* </Container> */}
            </Grid.Column>

            <Suspense fallback="loading">
              <Grid.Column width={11}>
                <div className="midcontainer">
                  <SearchBar {...props} />
                  <DashMidGrids changeFlag={changeFlag} {...props} />
                </div>
              </Grid.Column>
              <Grid.Column width={4} className="rightcontainer">
                {/* <Container> */}
                  <Profile className="Profile" />
                  <DashSideSection {...props}/>
                {/* </Container> */}
                {/* <MainButton {...props}>
                  {
                    t("ADD_ACTIVITY")
                  }
                </MainButton> */}
              </Grid.Column>
            </Suspense>
          </Grid.Row>
        </Grid>
      </Responsive>



      <Responsive minWidth={768} maxWidth={1365}>
        <TabletView {...props} />
      </Responsive>
    </div>
  );
};

export default Dashboard;
